import React, { Component } from 'react';
import './App.css';
import Timer from "./Timer";

class App extends Component {




  render() {
    return (
      <div className="App">
        <button onClick={this.startTimer}>Start timer</button>
        <div>{this.state.timer}</div>
        {this.state.timer === 0 && this.stopTimer()}
      </div>
    );
  }
}

export default App;
