import React, { Component } from 'react';
// import answers from "../answers.json";

class Radio extends Component {

  state = {
    selectedOption: " ",
    answers1: [
            {
                "label": "2001: A Space Odyssey",
                "value": false
            },
            {
                "label": "The Shining",
                "value": true
            },
            {
                "label": "One Flew Over the Cuckoo's Nest",
                "value": false
            },
            {
                "label": "The Godfather",
                "value": false
            }
        ],
        answers2: [
            {
                "name": "Luxo Ball",
                "class": "wrong"
            },
            { 
            "name": "Luxo",
            "class": "wrong"
            },
            {
            "name": "Luxo, Jr.",
            "class": "correct"
            },
            {
            "name": "Tinny",
            "class": "wrong"
            }
        ]
  };



  handleOptionChange = (event) => {
    event.preventDefault();
    console.log("clicked");
    // checked={true}
  };

  // ternery? if option in answerChoices[], add a button
//   create groups for each question
// for each item in a group, create a radio button with that name
// 


  render() {
    return (

          <div className="radio">

            <form>
                {this.state.answers1.map(answer => {
                return (
                    <input 
                        key={answer.id}
                        type="radio" 
                        value={answer.value} 
                        defaultChecked={false}
                        onClick={this.handleOptionChange} />
                    )
                })}
            </form>

          </div>



    );
  }
}

export default Radio;
