class Radio extends Component {

    state = {
        answerChoices,
        selectedOption: null,
        radioGroups: []
      }

  handleOptionChange = (event) => {
    event.preventDefault();
    console.log("clicked");
    // checked={true}
  };

  render() {
    return (

          <div className="radio">

            {answerChoices.map((answers, index) => (
            <div key={index}>
                <h1>{answers.label}</h1>
                {answers.value.map((value, i) => (
                <div key={i}>
                    <h3>{answers.label}</h3>
                    <hr />
                </div>
                ))}
            </div>
            ))}







          </div>

    );
  }
}

export default Radio;
